import React, { Component } from 'react';
import Days from './Days/Days';
import Hours from './Hours/Hours';
import List from './List/List';
import '../../App.css';

class Weather extends Component {
    state = {
        cities: [
            {id: '1', name: 'Budva', day: 'Subota', date:'09.07.2019', maxDayTemp: '24', minDayTemp: '14', maxNightTemp: '14', minNightTemp: '14', windSpeed: '15km/h', humidity: '41%',
            hourly:[
                {id:'1', time:'00', temperature:'B13'},
                {id:'2', time:'01', temperature:'B14'},
                {id:'3', time:'02', temperature:'B15'},
                {id:'4', time:'03', temperature:'B16'},
                {id:'5', time:'04', temperature:'B17'},
                {id:'6', time:'05', temperature:'B18'}
            ]},
            {id: '2', name: 'Podgorica', day: 'Nedelja', date:'09.07.2019', maxDayTemp: '25', minDayTemp: '14', maxNightTemp: '14', minNightTemp: '14', windSpeed: '15km/h', humidity: '41%',
            hourly:[
                {id:'1', time:'00', temperature:'P13'},
                {id:'2', time:'01', temperature:'P14'},
                {id:'3', time:'02', temperature:'P15'},
                {id:'4', time:'03', temperature:'P16'},
                {id:'5', time:'04', temperature:'P17'},
                {id:'6', time:'05', temperature:'P18'}
            ]},
            {id: '3', name: 'Niksic', day: 'Srijeda', date:'09.07.2019', maxDayTemp: '28', minDayTemp: '14', maxNightTemp: '14', minNightTemp: '14', windSpeed: '15km/h', humidity: '41%',
            hourly:[
                {id:'1', time:'00', temperature:'N13'},
                {id:'2', time:'01', temperature:'N14'},
                {id:'3', time:'02', temperature:'N15'},
                {id:'4', time:'03', temperature:'N16'},
                {id:'5', time:'04', temperature:'N17'},
                {id:'6', time:'05', temperature:'N18'}
            ]},
            {id: '4', name: 'Bar', day: 'Petak', date:'09.07.2019', maxDayTemp: '30', minDayTemp: '14', maxNightTemp: '14', minNightTemp: '14', windSpeed: '15km/h', humidity: '41%',hourly:[
                {id:'1', time:'00', temperature:'B13'},
                {id:'2', time:'01', temperature:'B14'},
                {id:'3', time:'02', temperature:'B15'},
                {id:'4', time:'03', temperature:'B16'},
                {id:'5', time:'04', temperature:'B17'},
                {id:'6', time:'05', temperature:'B18'}
            ]}
        ],
        showCityState: true
    };

    showHourly = (id) => {
        this.setState({showCityState: false});
        console.log(id);
        return 'true';
        //return this.state.cities[id].hourly;
    }



    render() {
        let cityId = this.showHourly;
        console.log(cityId);
        let hours = null;
        let days = null;
        if(this.state.showCityState === true){
        days = <Days 
                    cities={this.state.cities}
                    clicked={this.showHourly}/>
        }else{
            hours = <Hours
                    hours={this.state.cities[0].hourly}/>

        }
                
        

        let list = <List cities={this.state.cities}/>


        return (
        <div className="displayRow">

            <div className="dayContainer displayRow">
               {days}
               {hours}
            </div>

            <div className="list">
                {list}
            </div>
        
        </div>
        );
    }
}

export default Weather;
