import React, { Component } from 'react';
import Days from './Days/Days';
import '../../App.css';

class Weather extends Component {
    state = {
        cities: [
            {id: '1', name: 'Budva', day: 'Subota', temperature: '24'},
            {id: '2', name: 'Podgorica', day: 'Nedelja', temperature: '25'},
            {id: '3', name: 'Niksic', day: 'Srijeda', temperature: '28'},
            {id: '4', name: 'Bar', day: 'Petak', temperature: '30'}
        ]
    };
    render() {
        let days = null;
        days = <Day cities={this.state.cities}/>

        return (
    <div className="City-container">

        <div className="Day-container">
           {days}
        </div>

        <div className="Citys">
            <ul className="City-list">
                <li>Budva</li>
                <li>Podgorica</li>
                <li>Niksic</li>
                <li>Cetinje</li>
            </ul>
        </div>
        
    </div>
        );
    }
}

export default Weather;
