import React, { Component } from 'react';
import Days from './Days/Days';
import List from './List/List';
import '../../App.css';

class Weather extends Component {
    state = {
        cities: [
            {id: '1', name: 'Budva', day: 'Subota', date:'09.07.2019', maxDayTemp: '24', minDayTemp: '14', maxNightTemp: '14', minNightTemp: '14', windSpeed: '15km/h', humidity: '41%'},
            {id: '2', name: 'Podgorica', day: 'Nedelja', date:'09.07.2019', maxDayTemp: '25', minDayTemp: '14', maxNightTemp: '14', minNightTemp: '14', windSpeed: '15km/h', humidity: '41%'},
            {id: '3', name: 'Niksic', day: 'Srijeda', date:'09.07.2019', maxDayTemp: '28', minDayTemp: '14', maxNightTemp: '14', minNightTemp: '14', windSpeed: '15km/h', humidity: '41%'},
            {id: '4', name: 'Bar', day: 'Petak', date:'09.07.2019', maxDayTemp: '30', minDayTemp: '14', maxNightTemp: '14', minNightTemp: '14', windSpeed: '15km/h', humidity: '41%'}
        ],
        showCityState: true
    };
    render() {
        showHourly = (event, id) => {
            this.setState({showCityState: false});
        }



        let days = null;
        if(this.state.showCityState === true){
        days = <Days 
                    cities={this.state.cities}
                    clicked={this.showHourly}/>
                }

        let list = <List cities={this.state.cities}/>


        return (
        <div className="City-container">

            <div className="Day-container">
               {days}
            </div>

            <div className="List">
                {list}
            </div>
        
        </div>
        );
    }
}

export default Weather;
