import React, { Component } from 'react';
import axios from 'axios';
import Cities from './Cities/Cities';
import Days from './Days/Days';
import Hours from './Hours/Hours';
import List from './List/List';
import '../../App.css';

class Weather extends Component {
    state = {
        cities: [],
        showCityState: false,
        showDaysState: false,
        showHoursState: false,
        cityDaily: [],
        dayHourly: [],
        listElements:[]
    };

    componentDidMount(){
        axios.get('weather.json')
                .then(response => {
                    const citiesJSON = response.data.cities;
                    //console.log(citiesJSON);
                    this.setState({cities: citiesJSON});
                    //console.log(this.state.cities);
                });
    }

    showDaily = (id) => {
        //console.log("showDaily");
        this.setState({showCityState: true});

        let cityId = this.state.cities.findIndex(p => {
            return p.id === id;
        });

        this.setState({cityDaily: this.state.cities[cityId].daily});
        //console.log(this.state.cities[id].daily);
        //console.log(this.state.cityDaily);
        const citiesList = [...this.state.cities];
        citiesList.splice(cityId,1);
        //console.log(citiesList);
        const cityNames = citiesList.map((city) => {return {name: city.name, id:city.id}});
        //console.log(cityNames);
        this.setState({listElements: cityNames});

    }
    

    showHourly = (id) => {
        //console.log("showHourly");
        this.setState({showCityState: false});
        this.setState({showHoursState: true});

        let dayId = this.state.cityDaily.findIndex(p => {
            return p.id === id;
        });
        this.setState({dayHourly: this.state.cityDaily[dayId].hourly});
       // console.log(this.state.cityDaily[id].hourly);
       const daysList = [...this.state.cityDaily];
       daysList.splice(dayId,1);
       //console.log(citiesList);
       //console.log(daysList);
       const dayNames = daysList.map((dayel) => {return {name: dayel.day, id:dayel.id}});
       //console.log(cityNames);
       this.setState({listElements: dayNames});
    }

    returnToPrevious = (id) => {
        //console.log("returnToPrevious: " + id);
        // if(this.state.showCityState === false){
        //     this.setState({showCityState: true});
        // }
        //console.log(this.state.cityDaily);
        //console.log(this.state.showDaysState);
        //console.log(this.state.dayHourly);
        //console.log(this.state.showHoursState);
        if(this.state.showDaysState === false && this.state.showHoursState === true){
            this.showHourly(id);
            console.log("showHourly");
        }else if(this.state.showDaysState === false && this.state.showHoursState === false){
            this.showDaily(id);
            console.log("showDaily");
        }
    }



    render() {
        let hours = null;
        let days = null;
        let cities = null;
        let list = null;

        if(this.state.showCityState === false && this.state.showHoursState === false){
            cities = <Cities
                cities={this.state.cities}
                clicked={this.showDaily}/>
        } else if(this.state.showCityState === true && this.state.showHoursState === false){
            days = <Days 
                    cities={this.state.cityDaily}
                    clicked={this.showHourly}/>
        } else if(this.state.showCityState === false && this.state.showHoursState === true){
               hours = <Hours
                    hours={this.state.dayHourly}/>
        }else{
            console.log("Dosli smo do else");
        }

        list = <List elements={this.state.listElements}
                      clicked={this.returnToPrevious}
                      return = {this.return}/>


        return (
        <div className="displayRow">

            <div className="dayContainer displayRow">
                {cities}
               {days}
               {hours}
            </div>

            <div className="list">
                {list}
            </div>
        
        </div>
        );
    }
}

export default Weather;
