import React, { Component } from 'react';
import axios from 'axios';
import Cities from './Cities/Cities';
import Days from './Days/Days';
import Hours from './Hours/Hours';
import List from './List/List';
import '../../App.css';

class Weather extends Component {
    state = {
        cities: [],
        showCityState: false,
        showDaysState: false,
        showHoursState: false,
        city: [],
        day: []
    };

    componentDidMount(){
        axios.get('weather.json')
                .then(response => {
                    const citiesJSON = response.data.cities;
                    //console.log(citiesJSON);
                    this.setState({cities: citiesJSON});
                    //console.log(this.state.cities);
                });
    }

    showDaily = (id) => {
        //console.log("showDaily");
        this.setState({showCityState: true});
        this.setState({city: this.state.cities[id]});
        console.log(this.state.cities[id]);
        console.log(this.state.city);

    }
    

    showHourly = (id) => {
        console.log("showHourly");
        this.setState({showCityState: false});
        this.setState({showHoursState: true});
       // console.log(this.state.cities[id].hourly);
        this.setState({day: this.state.city.daily[id].hourly});
    }

    returnToPrevious = (id) => {
        //console.log("returnToPrevious: " + id);
        if(this.state.showCityState === false){
            this.setState({showCityState: true});
        }
    }



    render() {
        //console.log(this.state.cities);
        let hours = null;
        let days = null;
        let cities = null;
        let list = null;

        if(this.state.showCityState === false && this.state.showHoursState === false){
            cities = <Cities
                cities={this.state.cities}
                clicked={this.showDaily}/>
        } else if(this.state.showCityState === true && this.state.showHoursState === false){
            days = <Days 
                    cities={this.state.city}
                    clicked={this.showHourly}/>
        } else if(this.state.showCityState === false && this.state.showHoursState === true){
               // hours = <Hours
            //         hours={this.state.city}/>
            console.log("Prikaz satova");
        }else{
            console.log("Dosli smo do else");
        }


                
        

        list = <List elements={this.state.cities}
                        clicked={this.returnToPrevious}/>


        return (
        <div className="displayRow">

            <div className="dayContainer displayRow">
                {cities}
               {days}
               {hours}
            </div>

            <div className="list">
                {list}
            </div>
        
        </div>
        );
    }
}

export default Weather;
